import './Profile.css'
import React, { useEffect, useState } from 'react';

function Profile() {
  const [userInfo, setUserInfo] = useState(null);
  const [userID, setUserId] = useState(null);

  useEffect(() => {
    async function getUserInfo() {
      const response = await fetch('/api/user');
      const data = await response.json();
      setUserInfo(data);
      setUserId(data.UserID);
    }
    getUserInfo();
  }, []);

  const handleUpdateSubmit = async e => {
    e.preventDefault();

    const response = await fetch('/api/updateUser', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        UserID: userID,
        Username: e.target[0].value,
        Email: e.target[1].value,
        Password: e.target[3].value,
      }),
    });
    const data = await response.json();
    console.log(data);
  }

  return (
    <div className="profile-section">
      <h1>Profile</h1>
      <div className="tile-grid-container">
        <form className='user-info-div' onSubmit={handleUpdateSubmit}>
          {userInfo && (
            <>
              <label>Username:<br /> <input name='' type='text' value={userInfo.Username}/></label>
              <label>Email:<br /> <input name='' type='text' value={userInfo.Email}/></label>
              <label>Family:<br /> <input readOnly name='' type='text' value={userInfo.FamilyName}/></label>
              <label>Password:<br /> <input name='updatePass' type='text' value={undefined}/></label>
              <div></div>
              <input name='submit-update' type='submit' value='Update Info'/>
            </>
          )}
        </form>
      </div>
    </div>
  );
}

export default Profile;
