import './FamilySignUpForn.css';
import React, { useState } from 'react';
import './FamilySignUpForm.css';

function FamilySignUpForm() {
  const [isLoginVisible, setLoginVisible] = useState(true);
  const [familyName, setFamilyName] = useState('');
  const [familyPassword, setFamilyPassword] = useState('');

  const toggleVisibility = () => {
    setLoginVisible(!isLoginVisible);
  };

  const handleFamilyNameChange = (e) => {
    setFamilyName(e.target.value);
  };

  return (
    <div className='changingForm'>
      {isLoginVisible ? 
      <div className='the-form-div login-div'>
        <form>
          <label>
            Family Name:
            <input type='text' name='familyName' />
          </label>
          <label>
            Family Password:
            <input type='password' name='familyPassword' />
          </label>
          <input type='submit' value='Submit' />
        </form>
        <button onClick={toggleVisibility}>To Signup</button>
      </div> : 
      <div className='the-form-div signup-div'>
        <form>
          <label>
            Family Name:
            <input type='text' name='familyName' />
          </label>
          <label>
            Family Password:
            <input type='text' name='familyPassword' />
          </label>
        </form>
        <button onClick={toggleVisibility}>To Login</button>
      </div>}
    </div>
  );
};

export default FamilySignUpForm;
